# this shit gets the timetable and calculates the fitness
# how can we calculate the fitness?


def calculate_fitness(tt):
    # this function calculates the fitness of the timetable
    # the fitness is the gap between classes
    # extract each major level group of modules
    # print(tt)
    # lets get the modules in each major level group
    # arr = []
    fitness = 0

    dm = []
    yeet = []
    done = []
    for t in tt:
        major_level_group = []
        # get the modules in the major level group
        major = t['major']
        level = t['level']
        group = t['group']
        if ([major, level, group]) not in done:
            for m in tt:
                if m['major'] == major and m['level'] == level and m[
                        'group'] == group:
                    major_level_group.append({
                        'module': m['module'],
                        'days': m['days'],
                        'timeslots': m['timeslots']
                    })
            dm.append({
                'major': major,
                'level': level,
                'group': group,
                'modules': major_level_group
            })
            done.append([major, level, group])
    # print(dm)
    for x in dm:
        d = {0: [], 1: [], 2: [], 3: [], 4: []}
        for y in x['modules']:
            d[y['days'][0]].append(y['timeslots'][0])
            d[y['days'][1]].append(y['timeslots'][1])
        # sort the timeslots
        for k, v in d.items():
            d[k] = sorted(v)
        yeet.append(d)

    for x in yeet:
        for i in range(5):
            # print(x[i])
            if len(x[i]) == 1:
                # we only have one module scheduled for this day
                fitness -= 10
            elif len(x[i]) == 0:
                continue
            else:
                # we have more than one module scheduled for this day
                # we need to check if there is a gap between the modules
                for j in range(len(x[i]) - 1):
                    if x[i][j + 1] - x[i][j] > 1:
                        fitness -= 100
                    else:
                        fitness += 20
    if fitness < 0:
        print("FITNESS IS NEGATIVE")
        return fitness * -1
    print("FITNESS IS POSITIVE")
    print(fitness)
    print("\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n")
    return fitness

